INSERT INTO `shipping_clause` (`id`, `shipping_fee_clause_inside`, `shipping_fee_clause_outside`, `alias`) VALUES
(1, '<h2 style="font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 1.2em; font-weight: normal; margin-bottom: 5px; color: rgb(227, 120, 16) !important;">\r\n	Lieferung innerhalb Deutschlands</h2>\r\n<p style="color: rgb(0, 0, 0);">\r\n	Weiterf&uuml;hrende Informationen finden Sie auf folgenden Hilfeseiten:</p>\r\n<ul style="color: rgb(0, 0, 0);">\r\n	<li>\r\n		<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n			Internationale Lieferung</h3>\r\n	</li>\r\n	<li>\r\n		Lieferbeschr&auml;nkungen</li>\r\n	<li>\r\n		Berechnung von Lieferzeiten</li>\r\n</ul>\r\n<p>\r\n	<b style="color: rgb(0, 0, 0);">Bitte beachten Sie:</b><span style="color: rgb(0, 0, 0);">&nbsp;Die nachfolgenden Versandkosten und Lieferzeiten ab Versand beziehen sich auf Bestellungen, die direkt bei Tosello.tv get&auml;tigt werden. Informationen zu Bestellungen &uuml;ber unsere Plattform Tosello.tv Marketplace finden Sie auf unserer Hilfeseite&nbsp;</span>Lieferung von Tosello.tv Marketplace-Bestellungen<span style="color: rgb(0, 0, 0);">.</span></p>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<br />\r\n				<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n				</table>\r\n				<table border="1" cellpadding="6" cellspacing="0" width="99%">\r\n				</table>\r\n				<table border="1" cellpadding="5">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<b>Versandart</b></td>\r\n							<td>\r\n								<b>Versandkosten&nbsp;<br />\r\n								f&uuml;r&nbsp;</b>Tosello.tv Prime-Mitglieder</td>\r\n							<td>\r\n								<b>Versandkosten&nbsp;<br />\r\n								ohne Tosello.tv Prime-Mitgliedschaft</b></td>\r\n							<td>\r\n								Zustellung ab Versand<br />\r\n								(f&uuml;r Artikel mit der<br />\r\n								Verf&uuml;gbarkeitsangabe<br />\r\n								&quot;Auf Lager&quot;)</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Standardversand*</td>\r\n							<td>\r\n								--</td>\r\n							<td>\r\n								3,00 EUR pro Lieferung**<br />\r\n								(nur f&uuml;r Bestellungen unter 20,00 EUR<br />\r\n								und nicht f&uuml;r Artikel, die wir generell<br />\r\n								versandkostenfrei liefern)</td>\r\n							<td>\r\n								in 1 bis 2 Werktagen</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Premiumversand*</td>\r\n							<td>\r\n								kostenfrei</td>\r\n							<td>\r\n								6,00 EUR pro Lieferung**</td>\r\n							<td>\r\n								am n&auml;chsten Werktag</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Morning-Express</td>\r\n							<td>\r\n								5,00 EUR pro Artikel</td>\r\n							<td>\r\n								13,00 EUR pro Lieferung**</td>\r\n							<td>\r\n								am n&auml;chsten Werktag<br />\r\n								bis 12 Uhr</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Evening-Express</td>\r\n							<td>\r\n								5,00 EUR pro Artikel</td>\r\n							<td>\r\n								13,00 EUR pro Lieferung**</td>\r\n							<td>\r\n								am selben Tag Werktag<br />\r\n								zwischen 18 und 21 Uhr</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Spezialversand f&uuml;r Artikel ohne Jugendfreigabe</td>\r\n							<td>\r\n								5,00 EUR pro Lieferung**</td>\r\n							<td>\r\n								5,00 EUR pro Lieferung**</td>\r\n							<td>\r\n								in 1 bis 2 Werktagen</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Spezialversand f&uuml;r hochwertige Produkte</td>\r\n							<td>\r\n								kostenfrei</td>\r\n							<td>\r\n								kostenfrei</td>\r\n							<td>\r\n								am n&auml;chsten Werktag</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								Spezialversand f&uuml;r Gro&szlig;ger&auml;te</td>\r\n							<td>\r\n								<b>Standardlieferung:</b>&nbsp;kostenfrei f&uuml;r Lieferungen von Montag bis Freitag<br />\r\n								<b>Wunschterminlieferung:</b>kostenfrei f&uuml;r Lieferungen von Montag bis Freitag 7 bis 18 Uhr /&nbsp;<br />\r\n								33,00 EUR f&uuml;r Lieferungen von 18 bis 21 Uhr /&nbsp;<br />\r\n								25,00 EUR f&uuml;r Lieferungen am Samstag 8 bis 17 Uhr</td>\r\n							<td>\r\n								<b>Standardlieferung:</b>&nbsp;kostenfrei f&uuml;r Lieferungen von Montag bis Freitag<br />\r\n								<b>Wunschterminlieferung:</b>&nbsp;kostenfrei f&uuml;r Lieferungen von Montag bis Freitag 7 bis 18 Uhr / 33,00 EUR f&uuml;r Lieferungen von 18 bis 21 Uhr /<br />\r\n								25,00 EUR f&uuml;r Lieferungen am Samstag 8 bis 17 Uhr</td>\r\n							<td>\r\n								bei&nbsp;Standardlieferung&nbsp;nach Absprache mit dem Zusteller<br />\r\n								bei&nbsp;&nbsp;Wunschtermin-Lieferung<br />\r\n								gem&auml;&szlig; Terminplaner</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n				<p>\r\n					&nbsp;</p>\r\n				<p>\r\n					*In manchen F&auml;llen kann die Zustellung Ihrer Artikel bis zu 2 zus&auml;tzliche Tage in Anspruch nehmen. Dies ist auf der Produktseite des Artikels vermerkt und wird beim voraussichtlichen Lieferdatum in der Bestell&uuml;bersicht angegeben.</p>\r\n				<p>\r\n					**In manchen F&auml;llen fassen wir Artikel aus mehreren Bestellungen zu einer Lieferung zusammen. Weitere Informationen hierzu finden Sie auf unserer Hilfeseite&nbsp;Warum wurden meine Bestellungen in einer Lieferung zusammengefasst?</p>\r\n				Die jeweils g&uuml;ltige Umsatzsteuer ist in den Versandkosten enthalten.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Standardversand</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					Bestellungen, bei denen Sie Standardversand w&auml;hlen, werden per Deutsche Post DHL, DHL Express, Hermes, UPS oder PIN versendet.</p>\r\n				<p>\r\n					Bestellungen innerhalb Deutschlands liefern wir&nbsp;<b>ab einem Bestellwert von 20,00 EUR</b>&nbsp;in der Regel versandkostenfrei.</p>\r\n				<p>\r\n					B&uuml;cher (inkl. H&ouml;rb&uuml;cher und Kalender) liefern wir auch unter 20,00 EUR Bestellwert&nbsp;<b>kostenfrei</b>.</p>\r\n				<p>\r\n					Mehr Informationen zur Versandkostenfreiheit finden Sie auf unserer Hilfeseite&nbsp;Versandkostenfreie Lieferung.</p>\r\n				<p>\r\n					Einige Artikel k&ouml;nnen wir aufgrund ihrer Beschaffenheit und eines damit verbundenen Aufwands nicht kostenfrei liefern. Sie finden Informationen &uuml;ber die anfallenden Versandkosten direkt beim Artikel auf der Produktseite.</p>\r\n				<p>\r\n					<b>Wohin wir liefern</b>: An deutsche Lieferadressen, einschlie&szlig;lich&nbsp;deutsche Inseln. Eine Lieferung an eine&nbsp;Packstation&nbsp;ist m&ouml;glich.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Premiumversand</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					F&uuml;r fast alle Lieferadressen in Deutschland bieten wir f&uuml;r eilige Sendungen unseren Premiumversand an. Sie erhalten die Lieferung per Deutsche Post DHL, DHL Express, Hermes, UPS, Stadtbote oder PIN innerhalb eines Tages nach Versand, zu einem&nbsp;garantierten Liefertermin.</p>\r\n				<p>\r\n					<b>Hinweis zur Zustellung</b>: Die Zustellung erfolgt von Montag bis Samstag. An Samstagen kann in der Regel nicht an Firmenadressen zugestellt werden. An Sonntagen und an (regionalen wie bundesweiten) Feiertagen erfolgt keine Zustellung.</p>\r\n				<p>\r\n					<b>Wohin wir liefern</b>: An deutsche Lieferadressen, ausgenommen&nbsp;deutsche Inseln&nbsp;und Postfachadressen. Eine Lieferung an eine&nbsp;Packstation&nbsp;ist m&ouml;glich.</p>\r\n				<p>\r\n					<b>Artikel, die per Premiumversand versendet werden k&ouml;nnen</b>: Alle Artikel, bei denen die Versandart &quot;Premiumversand&quot; auf der Bestell&uuml;bersichtsseite ausw&auml;hlbar ist.</p>\r\n				<p>\r\n					<b>Hinweis zur Bestellung</b>: Wenn die von Ihnen gew&uuml;nschten Artikel alle auf Lager sind (dies sehen Sie an der Verf&uuml;gbarkeitsangabe&nbsp;<b>&quot;Auf Lager&quot;</b>) und Sie Ihre Bestellung fr&uuml;hzeitig aufgeben, erhalten Sie diese am Werktag nach Ihrer Bestellung (Bestellungen, die Sie am Sonntag aufgeben, erhalten Sie in der Regel am Dienstag). Ob Sie Ihre Bestellung am n&auml;chsten Werktag erhalten, sehen Sie, bevor Sie die Bestellung abschicken. Bei allen anderen Verf&uuml;gbarkeiten von mehreren Tagen oder Wochen verl&auml;ngert sich die Lieferzeit entsprechend.&nbsp;<br />\r\n					Auf unserer Hilfeseite&nbsp;Versand mit Liefergarantie&nbsp;erfahren Sie, wie Sie Artikel per Premiumversand bestellen.</p>\r\n				<p>\r\n					Bitte beachten Sie unsere&nbsp;Garantiebedingungen&nbsp;f&uuml;r den Premiumversand.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Morning-Express</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					Heute bestellen, am n&auml;chsten Werktag bis 12 Uhr erhalten! F&uuml;r fast alle Lieferadressen in Deutschland bieten wir f&uuml;r ganz eilige Sendungen unseren Morning-Express an. Sie erhalten die Lieferung per UPS, DHL Express oder Stadtbote am n&auml;chsten Vormittag, zu einem&nbsp;garantierten Liefertermin.</p>\r\n				<p>\r\n					<b>Hinweis zur Zustellung</b>: Die Zustellung erfolgt von Montag bis Samstag. An Samstagen kann in der Regel nicht an Firmenadressen zugestellt werden. An Sonntagen und an (regionalen wie bundesweiten) Feiertagen erfolgt keine Zustellung.</p>\r\n				<p>\r\n					<b>Wohin wir liefern</b>: An deutsche Lieferadressen, ausgenommen&nbsp;deutsche Inseln&nbsp;und Postfachadressen. Eine Lieferung an eine&nbsp;Packstation&nbsp;ist m&ouml;glich.</p>\r\n				<p>\r\n					<b>Artikel, die per Morning-Express versendet werden k&ouml;nnen</b>: Alle Artikel, die auf der Produktseite mit dem Hinweis &quot;Morning-Express&quot; gekennzeichnet sind und bei denen diese Versandart auf der Bestell&uuml;bersichtsseite ausw&auml;hlbar ist.</p>\r\n				<p>\r\n					<b>Hinweis zur Bestellung</b>: Bestellen Sie bitte innerhalb des auf der Produktseite angegebenen Zeitfensters. Nach Verstreichen des Zeitfensters wird Ihnen dort die n&auml;chstm&ouml;gliche Zustellung per Morning-Express angeboten. Ist eine Lieferung per Morning-Express ausgeschlossen, finden Sie keinen Hinweis auf diese Versandart auf der Produktseite des Artikels.<br />\r\n					Auf unserer Hilfeseite&nbsp;Versand mit Liefergarantie&nbsp;erfahren Sie, wie Sie Artikel per Morning-Express bestellen.</p>\r\n				<p>\r\n					Bitte beachten Sie unsere&nbsp;Garantiebedingungen&nbsp;f&uuml;r den Morning-Express.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Evening-Express</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					Heute bestellen, noch am selben Tag erhalten! F&uuml;r Adressen in&nbsp;bestimmten Postleitzahlbereichen&nbsp;bieten wir f&uuml;r ganz eilige Sendungen unseren Evening-Express an. Sie erhalten eine gro&szlig;e Anzahl unserer Artikel noch am selben Tag zwischen 18 Uhr und 21 Uhr per Deutsche Post DHL oder Stadtbote, zu einem&nbsp;garantierten Liefertermin.</p>\r\n				<p>\r\n					Auf unserer Hilfeseite&nbsp;Versand mit Liefergarantie&nbsp;erfahren Sie, wie Sie Artikel per Evening-Express bestellen.</p>\r\n				<p>\r\n					<b>Hinweis zur Zustellung</b>: Die Zustellung erfolgt von Montag bis Samstag. An Samstagen kann in der Regel nicht an Firmenadressen zugestellt werden. An Sonntagen und an (regionalen wie bundesweiten) Feiertagen erfolgt keine Zustellung.</p>\r\n				<p>\r\n					<b>Wohin wir liefern</b>: An deutsche Lieferadressen (ausgenommen Postfachadressen) und&nbsp;Packstationen&nbsp;(ausgenommen Stuttgart, Hannover, D&uuml;sseldorf, Leipzig und Umgebung) in&nbsp;bestimmten Postleitzahlbereichen.</p>\r\n				<p>\r\n					<b>Artikel, die per Evening-Express versendet werden k&ouml;nnen</b>: Alle Artikel, bei denen diese Versandart auf der Bestell&uuml;bersichtsseite ausw&auml;hlbar ist.<br />\r\n					Wenn in Ihren&nbsp;1-Click-Einstellungen&nbsp;eine f&uuml;r den Evening-Express berechtigte Lieferadresse als Standardadresse hinterlegt ist, sehen Sie bereits auf der Produktseite einen entsprechenden Hinweis, falls diese Option f&uuml;r den gew&uuml;nschten Artikel angeboten wird. Zus&auml;tzlich erscheint ein Zeitfenster, bis wann Sie den Artikel sp&auml;testens bestellen m&uuml;ssen, damit er rechtzeitig per Evening-Express geliefert werden kann.</p>\r\n				<p>\r\n					<b>Hinweis zur Bestellung</b>: Wenn eine Lieferung per Evening-Express f&uuml;r den gew&uuml;nschten Artikel angeboten wird, finden Sie einen entsprechenden Hinweis am Ende des Bestellformulars.</p>\r\n				<p>\r\n					Bitte beachten Sie unsere&nbsp;Garantiebedingungen&nbsp;f&uuml;r den Evening-Express.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Spezialversand f&uuml;r Artikel ohne Jugendfreigabe</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					Innerhalb Deutschlands ist f&uuml;r Filme und PC- &amp; Videospiele, die mit &quot;Keine Jugendfreigabe gem&auml;&szlig; &sect;14 JuSchG&quot; gekennzeichnet sind (oder gar keine Alterseinstufung erhalten haben), eine Lieferung an Minderj&auml;hrige nicht m&ouml;glich. Tosello.tv versendet solche Titel ausschlie&szlig;lich mit&nbsp;<b>DHL und Hermes Spezialversand f&uuml;r Artikel ohne Jugendfreigabe</b>.</p>\r\n				<p>\r\n					Branntwein, branntweinhaltige Getr&auml;nke und Lebensmittel, die Branntwein in nicht nur geringf&uuml;giger Menge enthalten, versendet Tosello.tv ebenfalls ausschlie&szlig;lich mit&nbsp;<b>Spezialversand f&uuml;r Artikel ohne Jugendfreigabe</b>.</p>\r\n				<p>\r\n					&nbsp;</p>\r\n				<p>\r\n					Bitte beachten Sie dazu unsere Hilfeseite&nbsp;Artikel ohne Jugendfreigabe&nbsp;sowie unsere&nbsp;Garantiebedingungen&nbsp;f&uuml;r diese Versandart.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Spezialversand f&uuml;r hochwertige Produkte</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					Der Versand&nbsp;<b>hochwertiger Uhren und Schmuck</b>&nbsp;erfolgt als Einzellieferung per DHL Express, unabh&auml;ngig davon, wie viele andere Artikel mitbestellt wurden. Entgegennehmen kann das Paket nur derjenige, dessen Name in der Paketadresse steht. Alternativ kann eine Person bevollm&auml;chtigt werden, wie etwa der Ehepartner, Lebensgef&auml;hrte oder ein Haushaltsmitglied. Bei der &Uuml;bergabe sind Unterschrift und Identit&auml;tspr&uuml;fung notwendig. Ein Versand an eine Packstation ist daher nicht m&ouml;glich.</p>\r\n				<p>\r\n					Mehr Informationen dazu finden Sie auf unserer Hilfeseite&nbsp;Uhren &amp; Schmuck.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; color: rgb(227, 120, 16) !important;">\r\n	Spezialversand f&uuml;r Gro&szlig;ger&auml;te</h2>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&nbsp;</h3>\r\n<table border="0" cellspacing="0" celpadding="5" style="color: rgb(0, 0, 0);" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;" valign="top">\r\n				<p>\r\n					F&uuml;r Produkte, die aufgrund ihres Gewichts, Volumens oder ihrer Beschaffenheit nicht auf dem normalen Postweg versandt werden k&ouml;nnen, bieten wir einen besonderen Lieferservice. Genauere Angaben hierzu finden Sie bei den jeweiligen Artikeln.</p>\r\n				<p>\r\n					Mehr Informationen dazu finden Sie auf unserer Hilfeseite&nbsp;Lieferung von Gro&szlig;ger&auml;ten.</p>\r\n				<div>\r\n					&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<h2 style="font-family: Arial, Verdana, Helvetica, sans-serif; line-height: 1.2em; font-weight: normal; margin-bottom: 5px; color: rgb(227, 120, 16) !important;">\r\n	Internationale Lieferung</h2>\r\n<p style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; line-height: 20px;">\r\n	Bitte lesen Sie unsere&nbsp;&nbsp;h&auml;ufig gestellten Fragen&nbsp;am Ende dieser Seite. Sie beinhalten wichtige Informationen zur internationalen Lieferung.</p>\r\n<p style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; line-height: 20px;">\r\n	Informationen zu&nbsp;<b>Z&ouml;llen</b>,&nbsp;<b>Steuern</b>&nbsp;und&nbsp;<b>Importgeb&uuml;hren</b>&nbsp;finden Sie auf unserer Hilfeseite&nbsp;Details zum Internationalen Versand.</p>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; line-height: 20px; color: rgb(227, 120, 16) !important;">\r\n	L&auml;nder innerhalb Europas</h2>\r\n<p style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; line-height: 20px;">\r\n	&Ouml;sterreich&nbsp;I&nbsp;Belgien,Niederlande&nbsp;I&nbsp;Luxemburg&nbsp;I&nbsp;Liechtenstein&nbsp;I&nbsp;Schweiz&nbsp;I&nbsp;D&auml;nemark&nbsp;I&nbsp;Frankreich&nbsp;I&nbsp;Gro&szlig;britannien&nbsp;I&nbsp;Norwegen&nbsp;I&nbsp;Polen&nbsp;I&nbsp;Tschechische Republik&nbsp;I&nbsp;T&uuml;rkei&nbsp;I&nbsp;&nbsp;Andere europ&auml;ische L&auml;nder Zone 1: Finnland, Italien, Portugal, Schweden, Spanien I&nbsp;Andere europ&auml;ische L&auml;nder Zone 2: Bulgarien, Estland, Griechenland. Lettland, Litauen, Malta, Rum&auml;nien, Slowenien, Slowakei, Ungarn, Zypern&nbsp;I&nbsp;Russland&nbsp;I&nbsp;&Uuml;brige europ&auml;ische L&auml;nder</p>\r\n<h2 style="border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 1.3em; margin-bottom: -0.2em; padding-bottom: 0px; padding-top: 1em; line-height: 20px; color: rgb(227, 120, 16) !important;">\r\n	L&auml;nder au&szlig;erhalb Europas</h2>\r\n<p style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; line-height: 20px;">\r\n	USA&nbsp;I&nbsp;Lateinamerika&nbsp;I&nbsp;Japan&nbsp;I&nbsp;Restliches Asien und Neuseeland&nbsp;I&nbsp;Mittlerer Osten&nbsp;I&nbsp;Australien&nbsp;I&nbsp;Afrika&nbsp;I&nbsp;&nbsp;Restliche L&auml;nder weltweit</p>\r\n<hr style="color: white; border-top-width: 0px; border-right-width: 0px; border-left-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(221, 221, 221); font-family: Arial; font-size: 13px; line-height: 20px;" />\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	&Ouml;sterreich</h3>\r\n<p style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; line-height: 20px;">\r\n	Detaillierte Informationen zum Versand nach &Ouml;sterreich finden Sie auf unserer Hilfeseite&nbsp;Lieferung nach &Ouml;sterreich.</p>\r\n<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n	Belgien, Niederlande</h3>\r\n<table border="1" cellpadding="5" style="color: rgb(0, 0, 0);">\r\n	<tbody>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				<h3 style="color: rgb(51, 51, 51); font-size: 1.1em; margin-bottom: 0px; padding-top: 7px; font-family: Arial; line-height: 20px;">\r\n					<b>Versandart</b></h3>\r\n			</td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				<b>Artikel</b></td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				<b>Zustellung ab Versand</b></td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				<b>Kosten pro Lieferung**</b></td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				Zus&auml;tzliche Kosten pro kg (anteilig)**</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				Standardversand</td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				B&uuml;cher (inkl. H&ouml;rb&uuml;cher<br />\r\n				und Kalender)</td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				in 2-3 Werktagen</td>\r\n			<td style="font-family: verdana, arial, helvetica, sans-serif; font-size: small;">\r\n				versandkostenfrei</td>\r\n			<td style=